<?php/*Template Name: Partners*/?><?php get_header();global $post;setup_postdata($post); ?>		<div class="content">			<div class="inner_content">				<div class="partners_heading widget widget_anim">					<h3 class="widget_title"><?php the_title(); ?></h3>					<p><?php echo strip_tags(get_the_content($post->ID),"<strong><em><br><a>"); ?></p>					<?php $contactpage = get_field("contact_page");if($contactpage->ID): ?>						<a href="<?php echo get_permalink($contactpage->ID); ?>"><?php _e("Contact us now","um_lang"); ?></a>					<?php endif; ?>				</div>						<div class="partners_field">						<?php							$partners = get_field("partners");							if($partners):							foreach($partners as $partner):							$image = "";							if($partner){								$image = $partner["partner_logo"]["url"];							}						?>						<div class="partner widget col_620 widget_anim">							<div class="partner_content">								<div class="partner_logo">									<img src="<?php echo $image; ?>"/>								</div>								<h4><?php echo $partner["partner_name"]; ?></h4>								<p><?php echo $partner["partner_description"]; ?></p>								<a href="<?php echo $partner["partner_website"]; ?>" target="_blank" class="widget_anim"><?php _e("Visit page","um_lang"); ?> <span></span></a>							</div>						</div>						<?php							endforeach;							endif;						?>											</div>			</div><!--Close Inner Content-->			</div><!--Close Content-->		<?php get_footer(); ?>	</div><!--Close Wrapper--></body></html>