<?php/*Template Name: Services*/?><?php get_header();$pageid=$post->ID; ?>		<div class="content">			<div class="inner_content">												<div class="mobile_accordion">					<div class="accordion" style="margin-bottom:20px;">						<ul>							<?php if(get_field("all_service_posts",$pageid)): ?>							<?php							$counter = 0;							$post_per_page = get_field("number_of_posts",$pageid);							if($post_per_page == '')								$post_per_page = 0;							$the_query = new WP_Query( array("post_type"=>"service_post","posts_per_page"=>$post_per_page) );							while ( $the_query->have_posts() ) : $the_query->the_post();							$categories = wp_get_post_terms($post->ID,"services_category");							if($categories){								$categories = $categories[0]->name;							}							else{								$categories = "";							}							?>							<li>								<h4><a href="#"><?php echo $categories; ?> <span></span></a></h4>								<div>									<h3><?php the_title(); ?></h3>									<?php the_content(); ?>								</div>							</li>										<?php endwhile; ?>							<?php else: ?>								<?php								$counter = 0;								$posts = get_field("service_posts",$pageid);								if($posts):								foreach( $posts as $post):								setup_postdata($post);								$categories = wp_get_post_terms($post->ID,"services_category");								if($categories){									$categories = $categories[0]->name;								}								else{									$categories = "";								}								?>								<li>									<h4><a href="#"><?php echo $categories; ?> <span></span></a></h4>									<div>										<h3><?php the_title(); ?></h3>										<?php the_content(); ?>									</div>								</li>								<?php endforeach;endif; ?>							<?php endif; ?>						</ul>					</div>				</div>												<?php if(get_field("bottom_text",$pageid)): ?>				<div class="sevice_text widget widget_anim">					<p><?php the_field("bottom_text",$pageid); ?></p>				</div>				<?php endif; ?>			</div><!--Close Inner Content-->		</div><!--Close Content-->		<?php get_footer(); ?>	</div><!--Close Wrapper--></body></html>