<?phpclass umbrella_twitterfeed_widget extends WP_Widget{	function umbrella_twitterfeed_widget() 	{		parent::WP_Widget(false, $name = 'Umbrella > Twitterfeed');	}		function widget($args, $instance)	{		extract( $args );		$title = apply_filters('widget_title', $instance['title']);		$twitter_search = $instance['twitter_search'];		?>		<div class="twitter_feed col_460 footer_col" twitter_search="<?php echo $twitter_search; ?>">			<h3><?php echo $title; ?></h3>			<div class="tweet_sample" style="display:none">				<a href="#"><img src="<?php bloginfo("template_directory"); ?>/images/icons/twitter_feed.png"></a>				<p class="tweet"><strong> </strong> </p>			</div>		</div><!--Close Twitter Feed-->		<script> jQuery("div.twitter_feed").twitterfeed(); </script>		<?php	}		function update($new_instance, $old_instance)	{		$instance = $old_instance;		$instance['title'] = strip_tags($new_instance['title']);		$instance['twitter_search'] = strip_tags($new_instance['twitter_search']);		return $instance;	}		function form($instance)	{		$title = isset($instance['title']) ? esc_attr($instance['title']) : "";		$twitter_search = isset($instance['twitter_search']) ? esc_attr($instance['twitter_search']) : "";		?>		<p>			<label for="<?php echo $this->get_field_id('title'); ?>"><?php _e('Widget Title'); ?></label>			<input class="widefat" id="<?php echo $this->get_field_id('title'); ?>" name="<?php echo $this->get_field_name('title'); ?>" type="text" value="<?php echo $title; ?>" />		</p>		<p>			<label for="<?php echo $this->get_field_id('twitter_search'); ?>"><?php _e('Twitter Screen Name (Without @)'); ?></label>			<input class="widefat" id="<?php echo $this->get_field_id('twitter_search'); ?>" name="<?php echo $this->get_field_name('twitter_search'); ?>" type="text" value="<?php echo $twitter_search; ?>" />		</p>		<?php	}}function register_umbrella_twitterfeed_widget() {				register_widget('umbrella_twitterfeed_widget');			}add_action('widgets_init', 'register_umbrella_twitterfeed_widget');?>